mapper.update:
update notice
   set count = count + 1
 where notice_no = #{noticeNo}

update notice
   set notice_title = #{noticeTitle},
   	   content = #{content},
   	   original_filename = #{originalFilename},
   	   change_filename = #{changeFilename}
 where notice_no = #{noticeNo}

update notice
   set notice_title = #{noticeTitle},
   	   content = #{content}
 where notice_no = #{noticeNo}
