mapper.update:
update question
   set count = count + 1
 where question_no = #{questionNo}

update question
   set question_title = #{questionTitle},
   	   content = #{content},
   	   original_filename = #{originalFilename},
   	   change_filename = #{changeFilename}
 where question_no = #{questionNo}

update question
   set question_title = #{questionTitle},
   	   content = #{content}
 where question_no = #{questionNo}

update question
   set question_status = '1'
 where question_no = #{refFromReply}
